void setup()
{
  pinMode(tonepin, OUTPUT);
  pinMode(A2, OUTPUT);
  digitalWrite(A2, LOW);
  pinMode(A3, OUTPUT);
  digitalWrite(A3, LOW);
  pinMode(A4, OUTPUT);
  digitalWrite(A4, LOW);
  pinMode(A5, OUTPUT);
  digitalWrite(A5, LOW);
  for (int i = 1; i <= 13; i++) {
    pinMode(i, OUTPUT);
    digitalWrite(i, LOW);
  }
  length = sizeof(scale) / sizeof(scale[0]);
}
void loop()
{
  for (int x = 0; x < length; x++)
  {
    if (scale[x] == 0) {
      noTone(tonepin);
    }
    else {
      tone(tonepin, scale[x]);
    }
    //digitalWrite(ledpin,HIGH);
    for (int i = 1; i <= 13; i++) {
      if (i % 4 == 1) {
        digitalWrite(i, HIGH);
      }
    }
    digitalWrite(A3, LOW);
    digitalWrite(A5, HIGH);
    for (int i = 1; i <= 13; i++) {
      if (i % 4 == 3) {
        digitalWrite(i, LOW);
      }
    }
    delay(660 * durt[x] / 4);
    //digitalWrite(ledpin,LOW);
    for (int i = 1; i <= 13; i++) {
      if (i % 4 == 1) {
        digitalWrite(i, LOW);
      }
    }
    digitalWrite(A5, LOW);
    digitalWrite(A3, HIGH);
    for (int i = 1; i <= 13; i++) {
      if (i % 4 == 3) {
        digitalWrite(i, HIGH);
      }
    }
    delay(150 * durt[x] / 4);
    noTone(tonepin);
  }
  delay(3000);
}
